#define BLUE 0xff004ce4
#define WHITE 0xffffffff
#define GET_COLOR(x) ((x) ? BLUE : WHITE)

static unsigned int draw_error_code = 0;
static HexColor drawer(int x, int y, int w, int h)
{
    // Avoid overflow by the division
    if (x == w)
        x--;

    if (y == h)
        y--;

    if (draw_error_code > 19)
    {
        return GET_COLOR(BSOD_SUS[BSOD_HEIGHT * y / h][BSOD_WIDTH * x / w]);
    }

    return GET_COLOR(BSODs[draw_error_code][BSOD_WIDTH * x / w + BSOD_WIDTH * (BSOD_HEIGHT * y / h)]);
}

void BSOD(int error_code)
{
    draw_error_code = error_code;
    drawShape(drawer, 0, 0, get_width(), get_height());
}
