#define BLUE 0xff004ce4
#define WHITE 0xffffffff
#define GET_COLOR(n, x) (((n) & (0x80 >> ((x) % 8))) ? WHITE : BLUE)

static unsigned int draw_error_code = 0;
static HexColor drawer(int x, int y, int w, int h)
{
    // Avoid overflow by the division
    if (x == w)
        x--;

    if (y == h)
        y--;

    int x_coordinate = BSOD_WIDTH * x / w;
    int y_coordinate = BSOD_HEIGHT * y / h;

    if (draw_error_code > 19)
    {
        return GET_COLOR(BSOD_SUS[y_coordinate][x_coordinate], (BSOD_WIDTH * 8) * x / w);
    }

    return GET_COLOR(BSODs[draw_error_code][x_coordinate + BSOD_WIDTH * y_coordinate], (BSOD_WIDTH * 8) * x / w);
}

void BSOD(int error_code)
{
    draw_error_code = error_code;
    drawShape(drawer, 0, 0, get_width(), get_height());
}
